mapper.insert:
INSERT INTO s_notice (

) VALUES (
      #{noticeNumber}
    , #{userId}
    , #{noticeTitle}
    , #{noticeContent}
    , sysdate
    , 0
    , NULL
    , NULL
    , 'N'
)

mapper.update:
UPDATE s_notice
SET
      notice_update_date = sysdate
    , notice_title = #{noticeTitle}
    , notice_content = #{noticeContent}
    , user_id = #{userId}
WHERE
    notice_number = #{noticeNumber}
